const getMaybeString = () => {
  if (Math.random() > 0.5) {
    'foo';
  } else {
    return undefined;
  }
};

export function upperCase(value: string) {
  return value.toUpperCase();
}

function thisMayFail() {
  // this expression only fails when strictNullChecks=true
  upperCase(getMaybeString());
}
